document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".header");new IntersectionObserver((function(t,s){t[0].isIntersecting?e.classList.remove("scroll"):e.classList.add("scroll")})).observe(e);const t=document.getElementById("video"),s=document.querySelector(".popup"),o=document.querySelector(".popup__video");t.addEventListener("click",e=>{e.preventDefault(),s.classList.add("show"),o.play()}),s.addEventListener("click",()=>{s.classList.remove("show"),o.pause()})});